#if FIM_USE_CXX14
template<typename T, typename S>
inline auto FIM_MIN(T x, S y)
{
	return ((x)<(y)?(x):(y));
}
template<typename T, typename S>
inline auto FIM_MAX(T x, S y)
{
	return ((x)>(y)?(x):(y));
}
#else /* FIM_USE_CXX14 */
#define FIM_MAX(x,y)        ((x)>(y)?(x):(y))
#define FIM_MIN(x,y)        ((x)<(y)?(x):(y))
#endif /* FIM_USE_CXX14 */

