/*
 * Fim language variable identifiers and help comment strings (notice their format).
 * Please note that the prefix FIM_VID is reserved to variable identifiers exclusively. 
 * */
//#define FIM_VID_NEWLINE 			"_newline"	/* "" */
//#define FIM_VID_TAB 				"_tab"	/* "" */
#define FIM_VID_ARCHIVE_FILES			"_archive_files"	/* "[in,g:] If non-empty, a regular expression matching the filenames to be treated as archives (multipage files). If empty, \"" FIM_CNS_ARCHIVE_RE "\" is used. Within each archive, only filenames matching the regular expression in the " FIM_VID_PUSHDIR_RE " variable are considered for loading." */
#define FIM_VID_RANDOM 				"random"	/* "[out] a pseudorandom number." */
#define FIM_VID_BINARY_DISPLAY 			"_display_as_binary"	/* "[in,g:] force loading files as pixelmaps (no image decoding is performed); if 1, using one bit per pixel; if 24, using 24 bits per pixel. If empty, load and decode the files as usual." */
#define FIM_VID_TEXT_DISPLAY 			"_display_as_rendered_text"	/* "[in,g:] if 1, force loading specified files as text files (no image decoding is performed); otherwise load and decode the files as usual." */
#define FIM_VID_CACHE_CONTROL 			"_cache_control"	/* "[in,g:] string for cache control. If it starts with 'm', cache mipmaps; if it starts with 'M' then do not not cache them. Otherwise defaults apply." */
#define  FIM_VID_CROP_ONCE 		"_crop_once"		/* "[in,g:] Unfinished." */
#define FIM_VID_CACHE_STATUS 			"_cache_status"		/* "[out,g:] string with current information on cache status." */
#define FIM_VID_DISPLAY_CONSOLE 		"_display_console"	/* "[in,g:] if 1, display the output console." */
#define FIM_VID_DEVICE_DRIVER 			"_device_string"	/* "[out,g:] the current display device string, in the form " FIM_DDN_VARS "." " See option --" FIM_OSW_OUTPUT_DEVICE " for a description." */
#define FIM_VID_DISPLAY_STATUS			"_display_status"	/* "[in,g:] if 1, display the status bar." */
#define FIM_VID_DISPLAY_STATUS_FMT		"_display_status_fmt"	/* "[in,g:] custom info format string, displayed in the lower left corner of the status bar; if unset: full pathname; otherwise a custom format string specified just as " FIM_VID_INFO_FMT_STR"." */
#define FIM_VID_PUSH_PUSHES_DIRS		"_push_pushes_dirs"	/* "[in,g:] if 1, the push command also accepts and pushes directories (using pushdir). if 2, also push hidden files/directories, that is, ones whose names begin with a dot (.)." */
#define FIM_VID_SANITY_CHECK			"_do_sanity_check"	/* "[in,experimental,g:] if 1, execute a sanity check on startup." */
#define FIM_VID_LAST_SYSTEM_OUTPUT		"_last_system_output"	/* "[out,experimental,g:] the standard output of the last call to the system command." */
#define FIM_VID_LAST_CMD_OUTPUT			"_last_cmd_output"	/* "[out,experimental,g:] the last command output." */
#define FIM_VID_LOAD_DEFAULT_ETC_FIMRC 		"_load_default_etc_fimrc"	/* "[in,g:] if 1 at startup, load the system wide initialization file." */
#define FIM_VID_LOAD_HIDDEN_DIRS 		"_load_hidden_dirs"	/* "[in,g:] if not 1, when pushing directories/files, those with name beginning with a dot (.) are skipped." */
#define FIM_VID_DEFAULT_ETC_FIMRC 		"_sys_rc_file"		/* "[in,g:] string with the global configuration file name." */
#if FIM_WANT_IMAGE_LOAD_TIME
#define FIM_VID_IMAGE_LOAD_TIME			"_file_load_time"	/* "[out,i:] time taken to load the file and decode the image, in seconds." */
#endif /* FIM_WANT_IMAGE_LOAD_TIME */
#define FIM_VID_FILE_LOADER 		"_file_loader"		/* "[in,i:,g:] specify a file loader to use (among the ones listed in the -V switch output); [out] i:" FIM_VID_FILE_LOADER " stores the loader of the current image." */
#define FIM_VID_FILE_BUFFERED_FROM 		"_buffered_in_tmpfile"		/* "[out,i:] if an image has been temporarily converted and decoded from a temporary file, its name is here." */
#define FIM_VID_EXTERNAL_CONVERTER 		"_external_decoder_program"	/* "[out,i:] if an image has been decoded via an external program, its name is here." */
#define FIM_VID_RETRY_LOADER_PROBE 		"_retry_loader_probe"		/* "[in,g:] if set to 1 and a user-specified file loader fails, probe for a different loader." */
#define FIM_VID_NO_RC_FILE			"_no_rc_file"		/* "[in,g:] if 1, do not load the ~/.fimrc configuration file at startup." */
#define FIM_VID_NO_EXTERNAL_LOADERS		"_no_external_loader_programs"		/* "[in,g:] if 1, do not attempt using external programs to decode a file of an unknown format." */
#define FIM_VID_SCRIPTOUT_FILE			"_fim_scriptout_file"	/* "[in,g:] the name of the file to write to when recording sessions." */
#define FIM_VID_PUSHDIR_RE			"_pushdir_re"	/* "[in] regular expression to match against when pushing files from a directory or an archive. By default this is \"" FIM_CNS_PUSHDIR_RE "\"." */
#define FIM_VID_STATUS_LINE 			"_status_line"		/* "[in,g:] if 1, display the status bar." */
#define FIM_VID_WANT_PREFETCH 			"_want_prefetch"	/* "[in,g:] if 1, prefetch further files just after displaying the first file; if 2 (and configured with --enable-cxx11) load in the background." */
#define FIM_VID_LOADING_IN_BACKGROUND		"_loading_in_background"	/* "[out,g:] 1 if program has been invoked with --" FIM_OSW_BGREC " and still loading in background. " */
